concepts.code:
while True:
    texto = input("Digite 'sair' para parar: ")
    if texto == "sair":
        break
    print("Você escreveu:", texto)

exercises.code_hint:
while True:
    nome = input("Digite seu nome (ou 'fim' para sair): ")
    if nome == "fim":
        break
    print(f"Olá, {nome}!")

while True:
    numero = int(input("Digite um número (0 para parar): "))
    if numero == 0:
        break
    print(f"Você digitou {numero}")
